#!/bin/bash

##############################################
# Cleans all test results data
#
# Data generated by test runs can become huge
# this script gets rid of all previous results.
#
##############################################

rm runs/*.php -rf
rm runs_samples/*.jtl -rf
rm runs_outputs/*.output
rm logs/*.log -rf

# Also images cache.
sudo rm cache -rf

outputinfo="
#######################################################################
Runs results deleted successfully.
"
echo "$outputinfo"
exit 0
